CREATE TABLE notes( itemID INTEGER PRIMARY KEY NOT NULL, ownerID INTEGER NOT NULL, shared INTEGER DEFAULT 1, isParent INTEGER DEFAULT 0, title TEXT, contentID INTEGER, parentID INTEGER NOT NULL DEFAULT 0, checked_out INTEGER DEFAULT 0, checked_out_time DATETIME DEFAULT NULL );
CREATE TABLE content( contentID INTEGER PRIMARY KEY NOT NULL, serial_content TEXT );
CREATE TABLE attach( contentID INTEGER PRIMARY KEY NOT NULL, attached BLOB );
CREATE TABLE secureds( itemID INTEGER PRIMARY KEY NOT NULL, ownerID INTEGER NOT NULL, shared INTEGER DEFAULT 0, isParent INTEGER DEFAULT 0, title TEXT, contentID INTEGER, parentID INTEGER NOT NULL DEFAULT 0, checked_out INTEGER DEFAULT 0, checked_out_time DATETIME DEFAULT NULL )